import React, { useEffect, useState } from "react";
import "./AdminDashboard.css";

export default function AdminDashboard() {
  const [loans, setLoans] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:8080/api/loans/all")
      .then(res => res.json())
      .then(data => {
        setLoans(data);
        setLoading(false);
      })
      .catch(err => {
        console.error("Error fetching loans:", err);
        setLoading(false);
      });
  }, []);

  if (loading) return <h2 className="loading">Loading loan applications...</h2>;

  return (
    <div className="admin-container">
      <h2 className="dashboard-title">Loan Applications Dashboard</h2>
      <div className="table-wrapper">

      <table className="loan-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Phone</th>
            <th>ID Number</th>
            <th>Loan Type</th>
            <th>Amount</th>
            <th>Verification Fee</th>
            <th>Status</th>
            <th>Tracking ID</th>
          </tr>
        </thead>

        <tbody>
          {loans.map((loan) => (
            <tr key={loan.id}>
              <td>{loan.id}</td>
              <td>{loan.name}</td>
              <td>{loan.phone}</td>
              <td>{loan.idNumber}</td>
              <td>{loan.loanType}</td>
              <td>Ksh {loan.loanAmount}</td>
              <td>Ksh {loan.verificationFee}</td>
              <td>
                <span className={`status ${loan.status.toLowerCase()}`}>
                  {loan.status}
                </span>
              </td>
              <td>{loan.trackingId}</td>
            </tr>
          ))}
        </tbody>
      </table>
      </div>
    </div>
  );
}
