import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LoanForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    idNumber: '',
    loanType: ''
  });
  const navigate = useNavigate();
  const [errors,seterrors]=useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

   const validate = () => {
    const newErrors = {};

    // Name: letters and spaces only
    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    } else if (!/^[A-Za-z ]+$/.test(formData.name)) {
      newErrors.name = 'Name can only contain letters and spaces';
    }

    // Phone: numbers only, 9-12 digits
    if (!formData.phone.trim()) {
      newErrors.phone = 'Phone number is required';
    } else if (!/^\d{9,12}$/.test(formData.phone)) {
      newErrors.phone = 'Phone number must be 9-12 digits';
    }

    // ID number: numeric only, 6-12 digits
    if (!formData.idNumber.trim()) {
      newErrors.idNumber = 'ID number is required';
    } else if (!/^\d{6,12}$/.test(formData.idNumber)) {
      newErrors.idNumber = 'ID number must be 6-12 digits';
    }

    // Loan type
    if (!formData.loanType) {
      newErrors.loanType = 'Please select a loan type';
    }

    setErrors(newErrors);

    // Return true if no errors
    return Object.keys(newErrors).length === 0;
  };

 const handleSubmit = async (e) => {
  e.preventDefault();
  if (!validate()) return;
  console.log('Submitting form data:', formData);
  try {
    const response = await fetch('http://localhost:8080/api/loans/apply', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    });
    const data = await response.json();
    navigate('/approval', { state: data });
  } catch (err) {
    console.error('Error submitting loan:', err);
  }
};

return (
  <form className="form-container" onSubmit={handleSubmit}>
      <h2 className="form-title">Find Your Loan Eligibility</h2>

      <div className="eligibility-info">
        <p>You can qualify for Ksh. 5,000 - 50,000 loan to MPESA</p>
      </div>

      <div className="form-group">
        <input
          type="text"
          name="name"
          placeholder="Your Name"
          value={formData.name}
          onChange={handleChange}
          className="form-input"
          required
        />
      </div>

      <div className="form-group">
        <input
          type="tel"
          name="phone"
          placeholder="Mpesa Phone Number"
          value={formData.phone}
          onChange={handleChange}
          className="form-input"
          required
        />
      </div>

      <div className="form-group">
        <input
          type="text"
          name="idNumber"
          placeholder="National ID Number"
          value={formData.idNumber}
          onChange={handleChange}
          className="form-input"
          required
        />
      </div>

      <div className="form-group">
        <select
          name="loanType"
          value={formData.loanType}
          onChange={handleChange}
          className="form-select"
          required
        >
          <option value="">Select Loan Type</option>
          <option value="emergency">Emergency Loan</option>
          <option value="topup">Top Up Loan</option>
          <option value="rental">Rental Loan</option>
          <option value="car">Car Loan</option>
        </select>
      </div>

      <p className="benefits-text">
        No CRB Check. No Guarantors. Disbursed to MPESA. No Paperwork
      </p>

      <button type="submit" className="submit-btn">
        FIND YOUR LOAN ELIGIBILITY
      </button>

      <div className="terms-section">
        <p className="terms-text">
          By submitting you confirm that you accept the{" "}
          <a href="#terms">Terms and Conditions</a> and{" "}
          <a href="#privacy">Privacy Policy</a>
        </p>
      </div>

      <div className="privacy-section">
        <p className="privacy-text">
          Protected by reCAPTCHA and subject to the Google{" "}
          <a href="#google-privacy">Privacy Policy</a> and{" "}
          <a href="#google-terms">Terms of Service</a>
        </p>
      </div>
  </form>
);

};

export default LoanForm;
