import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoanForm.css';

const LoanForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    idNumber: '',
    loanType: ''
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    navigate('/approval', { state: { formData } });
  };

  return (
    <div className="form-container"
    style={{
    backgroundImage: `url('/path-to-your-image.jpg')`,
    backgroundSize: 'cover',   // cover entire container
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    minHeight: '100vh',        // optional: make it full screen height
    padding: '2rem',            // optional padding
  }}>
      <h2 className="form-title">Find Your Loan Eligibility</h2>

      <div className="eligibility-info">
        <p>You can qualify for Ksh. 5,000 - 50,000 loan to MPESA</p>
      </div>

      <div className="form-group">
        <input
          type="text"
          name="name"
          placeholder="Your Name"
          value={formData.name}
          onChange={handleChange}
          className="form-input"
          required
        />
      </div>

      <div className="form-group">
        <input
          type="tel"
          name="phone"
          placeholder="Mpesa Phone Number"
          value={formData.phone}
          onChange={handleChange}
          className="form-input"
          required
        />
      </div>

      <div className="form-group">
        <input
          type="text"
          name="idNumber"
          placeholder="National ID Number"
          value={formData.idNumber}
          onChange={handleChange}
          className="form-input"
          required
        />
      </div>

      <div className="form-group">
        <select
          name="loanType"
          value={formData.loanType}
          onChange={handleChange}
          className="form-select"
          required
        >
          <option value="">Select Loan Type</option>
          <option value="emergency">Emergency Loan</option>
          <option value="topup">Top Up Loan</option>
          <option value="rental">Rental Loan</option>
          <option value="car">Car Loan</option>
        </select>
      </div>

      <p className="benefits-text">
        No CRB Check. No Guarantors. Disbursed to MPESA. No Paperwork
      </p>

      <button onClick={handleSubmit} className="submit-btn">
        FIND YOUR LOAN ELIGIBILITY
      </button>

      <div className="terms-section">
        <p className="terms-text">
          By submitting you confirm that you accept the{' '}
          <a href="#terms">Terms and Conditions</a> and{' '}
          <a href="#privacy">Privacy Policy</a>
        </p>
      </div>

      <div className="privacy-section">
        <p className="privacy-text">
          Protected by reCAPTCHA and subject to the Google{' '}
          <a href="#google-privacy">Privacy Policy</a> and{' '}
          <a href="#google-terms">Terms of Service</a>
        </p>
      </div>
    </div>
  );
};

export default LoanForm;
