import React from 'react';
import PhoneMockup from './PhoneMockup';
import LoanForm from './LoanForm';
import '../styles/LoanHomepage.css';

const LoanHomepage = () => {
  const [latest, setLatest] = useState(null);
  return (
    <div className="loan-container">
      {/* Left Section */}
      <div className="loan-left-section">
        <div>
          <div className="loan-logo">
            <p>KOPESHA</p>
            <p>LOAN</p>
          </div>

          <PhoneMockup />

          <h1 className="loan-heading">Kopesha Loan</h1>
          <p className="loan-description">
            Let us help you sort your expenses - top up a loan, rental loan, car loan or emergencies
          </p>
        </div>

          {/* Dynamic Transaction Card */}
        {latest && (
          <div className="transaction-card">
            <p className="transaction-name">
              {latest.name} {latest.phone.slice(0, 3)}xxx{latest.phone.slice(-3)}
            </p>

            <div className="transaction-amount">
              <span>Received Ksh.</span>
              <span className="amount-value">
                {latest.loanAmount.toLocaleString()}
              </span>
              <span className="loan-type">
                {latest.loanType.charAt(0).toUpperCase() + latest.loanType.slice(1)} Loan
              </span>
            </div>

            <p className="transaction-time">Just Now</p>
          </div>
        )}
         </div>

      {/* Right Section */}
      <div className="loan-right-section">
        <LoanForm />
      </div>
    </div>
  );
};

export default LoanHomepage;
