import React, { useEffect, useState } from 'react';
import '../styles/LoanApproval.css';

const LoanApproval = () => {
  const [applications, setApplications] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  // Fetch latest loan applications
  useEffect(() => {
    fetch('http://localhost:8080/api/loans/all')
      .then(res => res.json())
      .then(data => {
        if (data.length > 0) {
          // Sort latest first, take up to 10
          const sorted = data.sort((a, b) => b.id - a.id).slice(0, 10);
          setApplications(sorted);
        }
      })
      .catch(err => console.error('Error fetching loans:', err));
  }, []);

  // Rotate applications every 4 seconds
  useEffect(() => {
    if (applications.length <= 1) return;

    const interval = setInterval(() => {
      setCurrentIndex(prev => (prev + 1) % applications.length);
    }, 4000); // 4 seconds

    return () => clearInterval(interval);
  }, [applications]);

  if (applications.length === 0) {
    return <p style={{ textAlign: 'center', marginTop: '50px' }}>No applications yet</p>;
  }

  const app = applications[currentIndex];

  return (
    <div className="approval-container">
      <div className="approval-card fade-transaction">
        <div className="approval-header">
          <p>
            Hi {app.name}, you have qualified for a Loan of Ksh. {app.loanAmount.toLocaleString()} to your M-PESA.
            Your loan repayment period is 2 months with a 10% interest rate. Terms and conditions apply.
          </p>
        </div>

        <div className="approval-body">
          <div className="approval-table">
            <div className="approval-row">
              <span className="approval-label">Loan Tracking ID:</span>
              <span className="approval-value">{app.trackingId}</span>
            </div>
            <div className="approval-row">
              <span className="approval-label">Your Name:</span>
              <span className="approval-value">{app.name}</span>
            </div>
            <div className="approval-row">
              <span className="approval-label">MPESA Number:</span>
              <span className="approval-value">{app.phone}</span>
            </div>
            <div className="approval-row">
              <span className="approval-label">ID Number:</span>
              <span className="approval-value">{app.idNumber}</span>
            </div>
            <div className="approval-row">
              <span className="approval-label">Loan Type:</span>
              <span className="approval-value">{app.loanType.charAt(0).toUpperCase() + app.loanType.slice(1)} Loan</span>
            </div>
            <div className="approval-row">
              <span className="approval-label">Qualified Loan:</span>
              <span className="approval-value">Ksh. {app.loanAmount.toLocaleString()}</span>
            </div>
            <div className="approval-row">
              <span className="approval-label">Verification Fee:</span>
              <span className="approval-value">Ksh. {app.verificationFee}</span>
            </div>
          </div>

          <div className="approval-footer">
            <p className="approval-terms">
              By submitting you confirm that you accept the <a href="#terms">Terms and Conditions</a> and <a href="#privacy">Privacy Policy</a>
            </p>
            <button className="get-loan-btn">Get Loan Now</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoanApproval;
